        return false;
    }

    function addPermit(
        address from,
        address to,
        uint256 amount
    ) public {
        permittions[from][to] = amount;
    }

    function doTransferFrom(
        address token,
        address from,
        address to,
        uint256 amount
    ) public {
        IERC20(token).safeTransferFrom(from, to, amount);
    }

    function doTransfer(
        address token,
        address to,
        uint256 amount
    ) public {
        IERC20(token).safeTransfer(to, amount);
    }
}

