pragma solidity 0.8.9;
import "hardhat-deploy/solc_0.8/diamond/libraries/LibDiamond.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/IAccessControl.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "../libraries/BookingMapLib.sol";
import "../libraries/AccessControlLib.sol";
import "../libraries/MembershipLib.sol";
import "../libraries/StakeLibV2.sol";

// when upgrading AppStorage add new state vars to the end of the struct
// structs can only be upgraded if they are referred to within AppStorage by a mapping
struct AppStorage {
    bool initialized;
    // execution
    bool paused;
    // The ERC!!
    IERC20 communityToken;
    // Roles; the AccessControlLib.RoleStore struct can be upgraded
    AccessControlLib.RoleStore _roleStore;
    // Booking; the BookingMapLib.UserStore struct can be upgraded
    // BookingMapLib.YearsStore cannot be upgraded
    mapping(address => BookingMapLib.UserStore) _accommodationBookings;
    BookingMapLib.YearsStore _accommodationYears;
    // Stake; the OrderedStakeLib.Store struct can be upgraded
    mapping(address => OrderedStakeLib.Store) staking;
    // Members; the MembershipLib.Store struct cannot be upgraded
    MembershipLib.Store members;
    // Address of the TDF Treasury, used to make exception for transferring TDF while not yet go-live event
    address tdfTreasury;
}

library LibAppStorage {
    function diamondStorage() internal pure returns (AppStorage storage ds) {
        assembly {
            ds.slot := 0
        }
    }
}

contract Modifiers {
    using AccessControlLib for AccessControlLib.RoleStore;
    using MembershipLib for MembershipLib.Store;
    using StakeLibV2 for StakeLibV2.Context;
    using BookingMapLib for BookingMapLib.UserStore;
    using BookingMapLib for BookingMapLib.YearsStore;

    AppStorage internal s;

    modifier onlyOwner() {
        LibDiamond.enforceIsContractOwner();
        _;
    }

    modifier whenNotInitalized() {
        if (s.initialized) {
            revert("Already initialized");
        }
        _;
    }

    /**
     * @dev Modifier that checks that an account has a specific role. Reverts
     * with a standardized message including the required role.
     *
     * The format of the revert reason is given by the following regular expression:
     *
     *  /^AccessControl: account (0x[0-9a-f]{40}) is missing role (0x[0-9a-f]{64})$/
     *
     * _Available since v4.1._
     */
    modifier onlyRole(bytes32 role) {
        s._roleStore.checkRole(role);
        _;
    }

    /**
     * @dev Modifier to make a function callable only when the contract is not paused.
     *
     * Requirements:
     *
     * - The contract must not be paused.
     */
    modifier whenNotPaused() {
        _requireNotPaused();
        _;
    }

    /**
     * @dev Modifier to make a function callable only when the contract is paused.
     *
     * Requirements:
     *
     * - The contract must be paused.
     */
    modifier whenPaused() {
        _requirePaused();
        _;
    }

    modifier onlyMember() {
        _requireMember(_msgSender());
        _;
    }

    function _requireMember(address account) internal view {
        require(_isMember(account), "Membership: only members allowed");
    }

    function _isMember(address account) internal view returns (bool) {
        return s.members.contains(account);
    }

    /**
     * @dev Throws if the contract is paused.
     */
    function _requireNotPaused() internal view virtual {
        require(!s.paused, "Pausable: paused");
    }

    /**
     * @dev Throws if the contract is not paused.
     */
    function _requirePaused() internal view virtual {
        require(s.paused, "Pausable: not paused");
    }

    function _stakeLibContext(address account) internal view returns (StakeLibV2.Context memory) {
        return
            StakeLibV2.Context({account: account, token: s.communityToken, requiredBalance: _expectedStaked(account)});
    }

    function _stakeLibBookingContext(address account, uint16 year_)
        internal
        view
        returns (StakeLibV2.BookingContext memory)
    {
        (, BookingMapLib.Year memory year) = s._accommodationYears.get(year_);
        return
            StakeLibV2.BookingContext({
                account: account,
                token: s.communityToken,
                requiredBalance: _expectedStaked(account),
                initYearTm: year.start,
                endYearTm: year.end
            });
    }

    function _expectedStaked(address account) internal view returns (uint256) {
        uint256 max;
        BookingMapLib.Year[] memory _yearList = s._accommodationYears.values();
        for (uint16 i = 0; i < _yearList.length; i++) {
            // TODO: should it be + 1 year?
            if (_yearList[i].end < block.timestamp) continue;
            uint256 amount = s._accommodationBookings[account].getBalance(_yearList[i].number);
            if (amount > max) max = amount;
        }
        return max;
    }

    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}
","keccak256":"0x19a8346ad297427e4dbf018ef39080eb7830cf36f37f4df07363d2cabde27406","license":"MIT"},"src/diamond/libraries/BookingMapLib.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";

library BookingMapLib {
    using EnumerableMap for EnumerableMap.Bytes32ToUintMap;
    using EnumerableSet for EnumerableSet.Bytes32Set;

    enum BookingStatus {
        Pending,
        Confirmed,
        CheckedIn
    }

    struct Booking {
        BookingStatus status;
        uint16 year;
        uint16 dayOfYear;
        uint256 price;
        uint256 timestamp;
    }

    struct UserStore {
        mapping(uint16 => uint16) nights;
        mapping(uint16 => uint256) balance;
        mapping(uint16 => EnumerableMap.Bytes32ToUintMap) dates;
        mapping(bytes32 => Booking) bookings;
        // TODO: still not in use but added here to optimize proofOfPresence calculations
        mapping(uint16 => uint16) checkedInNights;
    }

    struct Year {
        uint16 number;
        bool leapYear;
        uint256 start;
        uint256 end;
        bool enabled;
    }

    struct YearsStore {
        EnumerableSet.Bytes32Set _inner;
        mapping(bytes32 => Year) elems;
    }

    function checkedInNightsOn(UserStore storage store, uint16 year_) internal view returns (uint16) {
        Booking[] memory localList = list(store, year_);
        uint16 acc;
        for (uint256 i; i < localList.length; i++) {
            if (localList[i].status == BookingMapLib.BookingStatus.CheckedIn) {
                acc++;
            }
        }
        return acc;
    }

    function add(UserStore storage store, Booking memory booking) internal returns (bool) {
        bytes32 key = _buildKey(booking.year, booking.dayOfYear);
        if (store.dates[booking.year].set(key, booking.timestamp)) {
            store.balance[booking.year] += booking.price;
            store.bookings[key] = booking; //Booking(booking.price, booking.timestamp);
            store.nights[booking.year] += uint16(1);
            return true;
        }
        return false;
    }

    /**
     * @dev Adds a booking, replacing an existing Pending booking if one exists.
     * @return success Whether the operation succeeded
     * @return replaced Whether an existing Pending booking was replaced
     * @return oldBooking The old booking that was replaced (only valid if replaced=true)
     */
    function addOrReplacePending(UserStore storage store, Booking memory booking)
        internal
        returns (
            bool success,
            bool replaced,
            Booking memory oldBooking
        )
    {
        bytes32 key = _buildKey(booking.year, booking.dayOfYear);

        // Check if booking already exists
        if (store.dates[booking.year].contains(key)) {
            Booking memory existing = store.bookings[key];

            // Only allow replacing Pending bookings
            if (existing.status != BookingStatus.Pending) {
                return (false, false, existing);
            }

            // Replace the pending booking - adjust balance for price difference
            store.balance[booking.year] = store.balance[booking.year] + booking.price - existing.price;
            store.bookings[key] = booking;
            store.dates[booking.year].set(key, booking.timestamp);
            return (true, true, existing);
        }

        // New booking
        if (store.dates[booking.year].set(key, booking.timestamp)) {
            store.balance[booking.year] += booking.price;
            store.bookings[key] = booking;
            store.nights[booking.year] += uint16(1);
            return (true, false, Booking(BookingStatus.Pending, 0, 0, 0, 0));
        }
        return (false, false, Booking(BookingStatus.Pending, 0, 0, 0, 0));
    }

    function get(
        UserStore storage store,
        uint16 _year,
        uint16 dayOfYear
    ) internal view returns (bool, Booking memory) {
        bytes32 key = _buildKey(_year, dayOfYear);
        if (store.dates[_year].contains(key)) {
            return (true, store.bookings[key]);
        }
        return (false, Booking(BookingStatus.Pending, 0, 0, 0, 0));
    }

    function getBalance(UserStore storage store, uint16 _year) internal view returns (uint256) {
        return store.balance[_year];
    }

    function getNights(UserStore storage store, uint16 _year) internal view returns (uint256) {
        return store.nights[_year];
    }

    function list(UserStore storage store, uint16 _year) internal view returns (Booking[] memory) {
        Booking[] memory bookings = new Booking[](store.dates[_year].length());
        for (uint256 i; i < store.dates[_year].length(); i++) {
            (bytes32 key, ) = store.dates[_year].at(i);
            bookings[i] = store.bookings[key];
        }
        return bookings;
    }

    function remove(
        UserStore storage store,
        uint16 _year,
        uint16 _dayOfYear
    ) internal returns (bool, Booking memory) {
        bytes32 key = _buildKey(_year, _dayOfYear);
        if (store.dates[_year].remove(key)) {
            Booking memory booking = store.bookings[key];
            store.balance[_year] -= booking.price;
            store.nights[_year] -= uint16(1);
            delete store.bookings[key];
            return (true, booking);
        }
        return (false, Booking(BookingStatus.Pending, 0, 0, 0, 0));
    }

    function updateStatus(
        UserStore storage store,
        uint16 _year,
        uint16 _dayOfYear,
        BookingStatus _status
    ) internal returns (bool) {
        bytes32 key = _buildKey(_year, _dayOfYear);
        if (store.dates[_year].contains(key)) {
            Booking memory booking = store.bookings[key];
            booking.status = _status;
            store.bookings[key] = booking;
            return true;
        }
        return false;
    }

    function _buildKey(uint16 year, uint16 dayOfYear) internal pure returns (bytes32) {
        return bytes32(abi.encodePacked(year, dayOfYear));
    }

    // ==========================================================
    // Years
    // ==========================================================
    function buildTimestamp(
        YearsStore storage _years,
        uint16 yearNum,
        uint16 dayOfTheYear
    ) internal view returns (bool, uint256) {
        (bool found, Year memory year) = get(_years, yearNum);
        if (found && year.enabled) {
            uint256 day;

            if (year.leapYear) {
                day = (year.end - year.start) / 366;
            } else {
                day = (year.end - year.start) / 365;
            }
            return (true, year.start + (day * (dayOfTheYear - 1)) + (day / 2));
        }
        return (false, uint256(0));
    }

    function buildBooking(
        YearsStore storage _years,
        BookingStatus status,
        uint16 yearNum,
        uint16 dayOfTheYear,
        uint256 price
    ) internal view returns (bool, Booking memory) {
        (bool success, uint256 tm) = buildTimestamp(_years, yearNum, dayOfTheYear);
        if (success) {
            return (true, Booking(status, yearNum, dayOfTheYear, price, tm));
        }
        return (false, Booking(BookingStatus.Pending, 0, 0, 0, 0));
    }

    /// YearsStore -------------------------------------------

    function length(YearsStore storage store) internal view returns (uint256) {
        return store._inner.length();
    }

    function add(YearsStore storage store, Year memory _year) internal returns (bool) {
        bytes32 k = _buildYearKey(_year.number);
        if (store._inner.add(k)) {
            store.elems[k] = _year;
            return true;
        }
        return false;
    }

    function values(YearsStore storage store) internal view returns (Year[] memory) {
        bytes32[] memory ks = store._inner.values();
        Year[] memory elems = new Year[](ks.length);
        for (uint256 i; i < ks.length; i++) {
            elems[i] = store.elems[ks[i]];
        }
        return elems;
    }

    function get(YearsStore storage store, uint16 num) internal view returns (bool, Year memory) {
        bytes32 k = _buildYearKey(num);
        if (store._inner.contains(k)) {
            return (true, store.elems[k]);
        }
        return (false, Year(0, false, 0, 0, false));
    }

    function remove(YearsStore storage store, uint16 num) internal returns (bool) {
        bytes32 k = _buildYearKey(num);
        if (store._inner.remove(k)) {
            delete store.elems[k];
            return true;
        }
        return false;
    }

    function contains(YearsStore storage store, uint16 num) internal view returns (bool) {
        bytes32 k = _buildYearKey(num);
        return store._inner.contains(k);
    }

    function update(YearsStore storage store, Year memory _year) internal returns (bool) {
        bytes32 k = _buildYearKey(_year.number);
        if (store._inner.contains(k)) {
            store.elems[k] = _year;
            return true;
        }
        return false;
    }

    // function length

    function _buildYearKey(uint16 num) internal pure returns (bytes32) {
        return bytes32(abi.encodePacked(num));
    }
}
